        <script>
            $(function(){
                $(document).on('submit', '#contact-form',function(e){
                    e.preventDefault();
                    let _this = $(this);
                    $.ajax({
                        type:"POST",
                        url:"<?= base_url('yttc-enquiry')?>",
                        beforeSend:function(){
                            _this.find('button[type="submit"]').text('Please Wait...');
                            _this.find('button[type="submit"]').prop('disabled', true);
                        },
                        data:_this.serialize(),
                        dataType: 'json',
                        success:function(response){
                            if(response.status){
                                $('.response').html(`<span class="success"> ${response.msg}</span>`)
                                 _this[0].reset();
                            }else{
                                $('.response').html(`<span class="error"> ${response.msg}</span>`)
                            }
                        },
                        complete:function(){
                           
                             _this.find('button[type="submit"]').text('Submit');
                            _this.find('button[type="submit"]').prop('disabled', false);
                        }
                    });
                });
            });
        </script>
    
